function q16  t = linspace(0, 10, 1000);  fig = figure;  hold on;  for k = [1, 1/2, 1/4]    y = phi(t, k);    plot(t, y, 'displayname', sprintf('k = %.2f', k));  endfor  y0 = us(t-4) .* sin(t-4);  plot(t, y0, 'displayname', '\Phi_0(t)');  hold off  ax  legend showendfunctionfunction y = phi(t, k)  y = t * 0;    t1 = t(t > 4-k & t < 4+k);  y(t > 4-k & t < 4+k) = (1 - cos(t1 - 4 + k)) / (2*k);    t2 = t(t >= 4+k);  y(t >= 4+k) = (cos(t2-4-k) - cos(t2-4+k)) / (2*k);endfunction